import{_ as t,c as a,b as r,o as i}from"./app-Bktcr98b.js";const n={};function o(s,e){return i(),a("div",null,e[0]||(e[0]=[r('<h1 id="software" tabindex="-1"><a class="header-anchor" href="#software"><span>Software</span></a></h1><h2 id="xampp" tabindex="-1"><a class="header-anchor" href="#xampp"><span>XAMPP</span></a></h2><p>XAMPP is een softwarepakket dat verschillende tools voor webontwikkeling bundelt in één applicatie.</p><p>XAMPP staat voor:</p><ul><li><strong>X</strong>: Werkt op elk besturingssysteem (Windows, Linux en macOS)</li><li><strong>A</strong>: Apache HTTP-server</li><li><strong>M</strong>: MariaDB Database Management System (~ MySQL)</li><li><strong>P</strong>: PHP programmeertaal</li><li><strong>P</strong>: Perl programmeertaal</li></ul><p>XAMPP kan gratis worden gedownload via de website van Apachefriends: <a href="https://www.apachefriends.org" target="_blank" rel="noopener noreferrer">https://www.apachefriends.org</a></p><p><a href="https://vives.gitbook.io/software-installation-guide/xampp" target="_blank" rel="noopener noreferrer">Installatiehandleiding</a></p><h2 id="mysql-workbench" tabindex="-1"><a class="header-anchor" href="#mysql-workbench"><span>MySQL Workbench</span></a></h2><p>MySQL Workbench is een verzameling tools die voornamelijk wordt gebruikt om MySQL- of MariaDB-databanken te maken en beheren via een grafische interface.</p><p><a href="https://www.mysql.com/products/workbench/" target="_blank" rel="noopener noreferrer">https://www.mysql.com/products/workbench/</a></p><h2 id="sqlite-browser" tabindex="-1"><a class="header-anchor" href="#sqlite-browser"><span>SQLite Browser</span></a></h2><p>SQLite Browser is een grafische gebruikersinterface om SQLite-databanken te maken en beheren.</p><p><a href="http://sqlitebrowser.org/" target="_blank" rel="noopener noreferrer">http://sqlitebrowser.org/</a></p><h2 id="lucid-chart" tabindex="-1"><a class="header-anchor" href="#lucid-chart"><span>Lucid Chart</span></a></h2><p>Lucid Chart is een online tool voor het maken van diagrammen en schema&#39;s voor softwareprojecten. Maak een gratis account aan met je VIVES e-mailadres en vraag vervolgens een gratis educatieve upgrade aan op <a href="https://www.lucidchart.com/pages/usecase/education" target="_blank" rel="noopener noreferrer">https://www.lucidchart.com/pages/usecase/education</a></p><p><a href="https://www.lucidchart.com/" target="_blank" rel="noopener noreferrer">https://www.lucidchart.com/</a></p><h3 id="git" tabindex="-1"><a class="header-anchor" href="#git"><span>GIT</span></a></h3><p>Voor versiebeheer en broncodebeheer gebruiken we GIT. Download en installeer GIT via de officiële GIT-website.</p><div class="hint-container tip"><p class="hint-container-title">Installatie-instellingen</p><p>Gebruik de standaardinstellingen in de installatiewizard, behalve bij &quot;Adjust your PATH environment settings&quot;. Dit MOET worden ingesteld op de derde optie: &quot;Use Git and optional Unix tools from the Windows Command Prompt&quot;.</p></div><p><a href="https://git-scm.com/" target="_blank" rel="noopener noreferrer">https://git-scm.com</a></p><p><a href="https://vives.gitbook.io/software-installation-guide/git/git" target="_blank" rel="noopener noreferrer">Installatiehandleiding</a></p><h2 id="posh-git" tabindex="-1"><a class="header-anchor" href="#posh-git"><span>Posh-Git</span></a></h2><p>PoshGit, of PowerShell Git, is een tool die PowerShell uitbreidt met handige functies voor het werken in een Git-project. Volg de installatie-instructies op de officiële pagina.</p><p><a href="https://github.com/dahlbyk/posh-git" target="_blank" rel="noopener noreferrer">https://github.com/dahlbyk/posh-git</a></p><p><a href="https://vives.gitbook.io/software-installation-guide/git/poshgit" target="_blank" rel="noopener noreferrer">Installatiehandleiding</a></p><h3 id="visual-studio-code" tabindex="-1"><a class="header-anchor" href="#visual-studio-code"><span>Visual Studio Code</span></a></h3><p>Visual Studio Code zal onze tekst- en code-editor zijn.</p><p><a href="https://code.visualstudio.com/" target="_blank" rel="noopener noreferrer">https://code.visualstudio.com/</a></p><p><a href="https://vives.gitbook.io/software-installation-guide/visual-studio-code" target="_blank" rel="noopener noreferrer">Installatiehandleiding</a></p>',29)]))}const h=t(n,[["render",o],["__file","index.html.vue"]]),p=JSON.parse('{"path":"/exercises/01-software-installation/","title":"Software","lang":"nl-BE","frontmatter":{"title":"Software"},"headers":[{"level":2,"title":"XAMPP","slug":"xampp","link":"#xampp","children":[]},{"level":2,"title":"MySQL Workbench","slug":"mysql-workbench","link":"#mysql-workbench","children":[]},{"level":2,"title":"SQLite Browser","slug":"sqlite-browser","link":"#sqlite-browser","children":[]},{"level":2,"title":"Lucid Chart","slug":"lucid-chart","link":"#lucid-chart","children":[{"level":3,"title":"GIT","slug":"git","link":"#git","children":[]}]},{"level":2,"title":"Posh-Git","slug":"posh-git","link":"#posh-git","children":[{"level":3,"title":"Visual Studio Code","slug":"visual-studio-code","link":"#visual-studio-code","children":[]}]}],"git":{"updatedTime":1738234077000,"contributors":[{"name":"verwo","username":"verwo","email":"wouter_verstraete@hotmail.com","commits":2,"url":"https://github.com/verwo"}]},"filePathRelative":"exercises/01-software-installation/index.md"}');export{h as comp,p as data};
